import React from 'react';
import Hero from '../components/Hero';
import Content from '../components/Content';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Axios from 'axios';


class ContactPage extends React.Component {

    constructor(props){
        super(props);
        this.stae={
            name: '',
            email: '',
            message: '',
            disabled: false,
            emailSent: null,
        }
    }
    render(){
    return(
        <div>
            <Hero title={this.props.title}/>
             <Content>
                <Form onSubmit={this.handleSubmit}>
                <Form.Group>
                    <Form.Label htmlFor="full-name">Full Name</Form.Label>
                    <Form.Control id="full-name" name="name" type="text"  onChange={this.handleChange} />
                </Form.Group>

                <Form.Group>
                    <Form.Label htmlFor="email">Email</Form.Label>
                    <Form.Control id="email" name="email" type="email"  onChange={this.handleChange} />
                </Form.Group>

                <Form.Group>
                    <Form.Label htmlFor="message">Message</Form.Label>
                    <Form.Control id="message" name="message" as="textarea" rows="3" onChange={this.handleChange} />
                </Form.Group>

                <Button className="d-inline-block" variant="primary" type="submit">
                            Send
                </Button>

                 {this.state.emailSent === true && <p className="d-inline success message">Email sent successfully</p>}
                 {this.state.emailSent === false && <p className="d-inline err message">Email NOT sent successfully</p>}   
                </Form>
             </Content>
        </div>
    );
    }
}

export default ContactPage;
