import React from 'react';
import Hero from '../components/Hero';
import Content from '../components/Content';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Axios from 'axios';




class ContactPage extends React.Component {

    constructor(props) {
        super(props);
        this.stae={
            name: '',
            email: '',
            message: '',
            disabled: false,
            emailSent: null,
        }
    }



    handleChange = (event) => { 
    console.log(event);

    const target = event.target;
    const value = target.type ==='checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
        [name]: value
    });
}

    handleSubmit = (event) => {
        event.preventDefault();

        this.setState({
            disabled: true

        });


      Axios.post('/api/email',this.state)
            .then(res => { 
                this.setState({
                    disabled: false,
                    emailSent: true,
                });
            })

            .catch(err => {
                this.setState({
                    disabled: false,
                    emailSent: false,

                });
            })

    }
    render() {
    return(
        <div>
            <Hero title={this.props.title}/>
             <Content>
                <Form onSubmit={this.handleSubmit}>
                <Form.Group>
                    <Form.Label htmlFor="full-name">Full Name</Form.Label>
                    <Form.Control id="full-name" name="name" type="text" onChange={this.handleChange}/>
                </Form.Group>

                <Form.Group>
                    <Form.Label htmlFor="email">Email</Form.Label>
                    <Form.Control id="email" name="email" type="email"  />
                </Form.Group>

                <Form.Group>
                    <Form.Label htmlFor="message">Message</Form.Label>
                    <Form.Control id="message" name="message" as="textarea" rows="4"  />
                </Form.Group>



                <Button className="d-inline-block" varient="primary" type="submit">
                    Send
                </Button>





                </Form>
             </Content>
        </div>
    );
    }
}

export default ContactPage;
